mapper.select:
SELECT pk_id, company_id, user_id, theme, classify_id, classify_name, type, num, city, experience, education, description, salary_start, salary_end, salary_count, time_start, time_end, status, time_create, time_feedback, time_change, is_deleted FROM recruit WHERE theme like CONCAT('%', #{q}, '%') AND city like CONCAT('%', #{city}, '%')
AND experience = #{experience}
AND education = #{education}
AND ( (salary_start <= #{salaryStart} AND salary_end > #{salaryEnd}) OR (salary_end >= #{salaryStart} AND salary_end < #{salaryEnd}) ) LIMIT #{index}, #{pageSize};

SELECT COUNT(*)
FROM recruit
WHERE theme like CONCAT('%', #{q}, '%')
  AND city like CONCAT('%', #{city}, '%')

    AND experience = #{experience}


    AND education = #{education}

  AND (
        (salary_start <= #{salaryStart} AND salary_end > #{salaryEnd})
        OR (salary_end >= #{salaryStart} AND salary_end < #{salaryEnd})
    );
